@import "base";

.projects {
  &__header{
    font-size: 50px;
    font-style: italic;
    padding-bottom: 10px;
  }
}
.project{
  &__header {
    display: grid;
    grid-template-columns: 1fr 3fr;
    gap: 1rem;
    font-family: sans-serif;
    & span {
      border-top: 1px solid $white-color;
      padding: 1rem;
      font-size: 25px;
      text-align: center;
    }
  }
  &__infos{
    margin-top: 1rem;
    &--name {
      position: relative;
      font-size: 2rem;
      font-weight: 200;
      font-family: sans-serif;
      display: flex;
      align-items: center;
    }
  }
  &__img{
    position: relative;
    width: 100%;
    img{
      max-width: 100%;
      height: auto;
      object-fit: cover;
      border-radius: 15px;
    box-shadow: 0 8px 32px 0 $box-shadow-blue2;
    position: absolute;
    right: 10vw;
    top: 3rem;
    }
    //@media screen and (min-width: 600px) {
    //  img {
    //    max-width: 50%;
    //    height: auto;
    //  }
    //}
  }

  &__links{
    padding-top: 3rem;
    button{
    &:before{
      background: $dark-blue;
    }
      &:after{
        display: none;
      }

    }


  }
}
